import React from "react";
import "./Step.css";

const Step = (props) => {
  let inputStep = 0;
  return () => {
    <div className="step">
      <input
        ref={(data) => {
          this._inputStep = data;
        }}
        onChange={() => {
          props.updateStep(inputStep.value);
        }}
        type="number"
        value={props.stepValue}
      />
    </div>;
  };
};

export default Step;
